import { useState, useContext } from 'react'; //tracks data

import Button from '../button/button.component';
import FormInput from '../form-input/form-input.component';

import { userContext } from '../contexts/user.context';

import './sign-in-form.styles.scss';

import {
	signInWithGooglePopup,
	createUserDocumentFromAuth,
	createAuthUserWithEmailAndPassword,
	signInAuthUserWithEmailAndPassword,
} from '../../utils/firebase/firebase.utils';

const defaultFormFields = {
	email: '',
	password: '',
};

const signInWithGoogle = async () => {
	const { user } = await signInWithGooglePopup();
	await createUserDocumentFromAuth(user);
};

/*  destruct form to pickup state change */
const SignInForm = () => {
	const [formFields, setFormFields] = useState(defaultFormFields);
	const { email, password } = formFields;

	const { setCurrentUser } = useContext(userContext);

	/* Resets form fields*/
	const resetFormFields = () => {
		setFormFields(defaultFormFields);
	};

	/* Handles creation of user when Submit is pressed - Start*/
	const handleSubmit = async (event) => {
		event.preventDefault();
		try {
			const response = await signInAuthUserWithEmailAndPassword(
				email,
				password
			);

			resetFormFields();
		} catch (error) {
			switch (error.code) {
				case 'auth/wrong-password':
					alert('incorrect password for email');
					break;
				case 'auth/user-not-found':
					alert('no user associated with this email');
					break;
				default:
					console.log(error);
			}
		}
	};
	/* Handles creation of user when Submit is pressed - Start*/
	/* Updates form Display with input values - Start*/
	const handleChange = (event) => {
		const { name, value } = event.target;
		setFormFields({ ...formFields, [name]: value });
	};
	/* Updates form Display with input values - Start*/

	/* Sign up form layout - Start*/
	return (
		<div className="sign-in-container">
			<h2>Already have a Acount</h2>
			<span>Sign in with your email and password</span>
			<form onSubmit={handleSubmit}>
				<FormInput
					label="Email"
					type="email"
					required
					onChange={handleChange}
					name="email"
					value={email}
				/>

				<FormInput
					label="Password"
					type="password"
					required
					onChange={handleChange}
					name="password"
					value={password}
				/>
				<div className="buttons-container">
					<Button type="submit">Sign In</Button>
					<Button
						buttonType="google"
						type="button"
						onClick={signInWithGoogle}>
						Google Sign In
					</Button>
				</div>
			</form>
		</div>
	);

	/*Sign up form layout - End*/
};

export default SignInForm;
