import { useState } from 'react'; //tracks data

import Button from '../button/button.component';
import FormInput from '../form-input/form-input.component';
import './sign-in-form.styles.scss';

import {
	createAuthUserWithEmailAndPassword,
	createUserDocumentFromAuth,
	signInWithGooglePopup,
} from '../../utils/firebase/firebase.utils';

const defaultFormFields = {
	email: '',
	password: '',
};

const signInWithGoogle = async () => {
	const { user } = await signInWithGooglePopup();
	const userDocRef = await createUserDocumentFromAuth(user);
};

/*  destruct form to pickup state change */
const SignInForm = () => {
	const [formFields, setFormFields] = useState(defaultFormFields);
	const { email, password } = formFields;

	/* Resets form fields*/
	const resetFormFields = () => {
		setFormFields(defaultFormFields);
	};

	/* Handles creation of user when Submit is pressed - Start*/
	const handleSubmit = async (event) => {
		event.preventDefault();
		try {
		} catch (error) {}
	};
	/* Handles creation of user when Submit is pressed - Start*/
	/* Updates form Display with input values - Start*/
	const handleChange = (event) => {
		const { name, value } = event.target;
		setFormFields({ ...formFields, [name]: value });
	};
	/* Updates form Display with input values - Start*/

	/* Sign up form layout - Start*/
	return (
		<div className="sign-up-cantainer">
			<h2>`Already have a account</h2>
			<span>Sign up with your email and password</span>
			<form onSubmit={handleSubmit}>
				<FormInput
					label="Email"
					type="email"
					required
					onChange={handleChange}
					name="email"
					value={email}
				/>

				<FormInput
					label="Password"
					type="password"
					required
					onChange={handleChange}
					name="password"
					value={password}
				/>
				<Button type="submit">Sign In</Button>
				<Button
					buttonType="google"
					onClick={signInWithGooglePopup}>
					Google Sign In
				</Button>
			</form>
		</div>
	);

	/*Sign up form layout - End*/
};

export default SignInForm;
