import { useState } from "react"; //tracks data
import formInput from "../form-input/form-input.component";
import { createAuthUserWithEmailAndPassword, createUserDocumentFromAuth } from "../../utils/firebase/firebase.utils";

const defaultFormFields = {
    displayName: ' ',
    email: '',
    password: '',
    confirmpassword: '',

};

/*  destruct form to pickup state change */
const SignUpForm = () => {
    const [formFields, setFormfields] = useState(defaultFormFields);
    const { displayName, email, password, confirmPassword } = formFields;


    /* Resets form fields*/
    const resetFormFields = () => {
        setFormfields(defaultFormFields); 
    };


    /* Handles creation of user when Submit is pressed - Start*/
    const handleSubmit = async (event) => {
        event.preventDefault()

        if (password !== confirmPassword) {
            alert("Password does not match");
            return;
        }

        try {
            const { user } = await createAuthUserWithEmailAndPassword(
                email,
                password
            );
            await createUserDocumentFromAuth(user, { displayName });
            resetFormFields();
        } catch(error) {    
            if (error.code === 'auth/email-already-in-use') {
                alert('Cannot create user, email already in use');
            }
            else
            {
                console.log('user error', error);
            };
        };
    };
    /* Handles creation of user when Submit is pressed - Start*/

    /* Updates form Display with input values - Start*/
    const handleChange = (event) => {
        const { name, value } = event.target;

        setFormfields({...formFields, [name]: value});

    };
    /* Updates form Display with input values - Start*/


    /* Sign up form layout - Start*/
    return (
        <div>
            <h1>Sign up with your email and password</h1>
            <form onSubmit={() => {}}>
                <fromInput 
                    label='Display Name'
                    type="text" 
                    required onChange={handleChange} 
                    name='displayName' 
                    value={displayName} 
                />

                <fromInput 
                    label='Email'
                    type="email" 
                    required 
                    onChange={handleChange} 
                    name='email' 
                    value={email} 
                />

                <fromInput 
                    label='Password'
                    type="password" 
                    required 
                    onChange={handleChange} 
                    name='password' 
                    value={password} 
                />

                <fromInput 
                    label='Confirm Password'
                    type="password" 
                    required 
                    onChange={handleChange} 
                    name='confirmPassword' 
                    value={confirmPassword} 
                />
                <button type="submit">Sign Up</button>

            </form>
        </div>
    );

    /*Sign up form layout - End*/

};

export default SignUpForm;
