import styled from 'styled-components';

export const CartIconContainer = styled.div`
	width: 45px;
	height: 45px;
	position: relative;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
`;

export const ShoppingIcon = styled.div`
	width: 0px;
	height: 0px;
`;

export const ItemCount = styled.div`
	position: absolute;
	font-size: 10px;
	font-weight: bold;
	bottom: 12px;
`;
