import styled from 'styled-components';

export const CategoryContainer = styled.div`
	display: grid;
	grid-template-columns: repeat(4, 1fr);
	column-gap: 20px;
	row-gap: 10px;
`;

export const Title = styled.div`
	font-size: 38px;
	margin-bottom: 20px;
	align-content: center;
	text-align: center;
`;
